import React from "react";
import { useState, useEffect } from "react";
import { rutaAPI } from "../../config/Config";

import ProspectoModal from "./presentation/ProspectoModal";

export default function Modalcontainer({ prospecto }) {
  const [data, setData] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    // console.log("prospecto", prospecto);
    const fetchResource = async () => {
      try {
        let res = await fetch(`${rutaAPI}/mostrar-prospecto/${prospecto}`);
        let data = await res.json();

        console.log(data.data[0]);
        setData(data.data[0]);
      } catch (error) {
        setError(error);
      }
    };
    fetchResource();
  }, [prospecto]);

  handleSubmit = async (e) => {
    e.preventDefault();

    console.log(formData);

    let config = {
      method: "PUT",
      body: data,
    };
    let res = await fetch(`${rutaAPI}/edit-prospecto/${data._id}`, config);
    let json = await res.json();
    console.log(json);

    if (json.status == 200) {
      this.props.history.push("/");
    }
  };
  handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  return (
    <ProspectoModal onChange={handleChange} onSubmit={handleSubmit} {...data} />
  );
}
