import React, { Fragment } from "react";
import { rutaAPI } from "../../config/Config";
import Encabezado from "../../components/Encabezado";
import Tabla from "../../components/tabla/Tabla";
import { Link } from "react-router-dom";

class Prospecto extends React.Component {
  state = {
    data: [],
    error: null,
  };

  async componentDidMount() {
    await this.fetchProspectos();
  }

  fetchProspectos = async () => {
    try {
      let res = await fetch(`${rutaAPI}/mostrar-prospecto`);
      let data = await res.json();

      if (data.status === 200) {
        this.setState({
          data: data.data,
        });
      }
    } catch (error) {
      this.setState({
        error,
      });
    }
  };

  render() {
    return (
      <Fragment>
        <Encabezado titulo="Prospectos" />
        <div className="row">
          <div className="card card-primary card-outline">
            <div className="card-header">
              <Link to="/prospecto/new">
                <button className="btn btn-primary">
                  Agregar Nuevo Prospecto
                </button>
              </Link>
            </div>
            <div className="card-body">
              <Tabla
                cabeceras={[
                  "#",
                  "Nombre",
                  "Calle",
                  "Colonia",
                  "Código Postal",
                  "Teléfono",
                  "RFC",
                  "Estado",
                  "Acciones",
                ]}
                prospectos={this.state.data}
              />
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}
export default Prospecto;
