import React, { Fragment } from "react";
import Encabezado from "../../components/Encabezado";
import ProspectoForm from "./ProspectoForm";
import { rutaAPI } from "../../config/Config";

class ProspectoNew extends React.Component {
  state = {
    form: {
      Prospect_Name: "",
      Prospect_FName: "",
      Prospect_LName: "",
      Prospect_Calle: "",
      Prospect_NumDom: "",
      Prospect_Col: "",
      Prospect_PostCode: "",
      Prospect_Tel: "",
      Prospect_RFC: "",
      documento: "",
    },
    error: null,
  };

  handleChange = (e) => {
    this.setState({
      form: { ...this.state.form, [e.target.name]: e.target.value },
    });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    console.log(this.state.form);
    try {
      let config = {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(this.state.form),
        files: this.state.form.documento,
      };
      let res = await fetch(`${rutaAPI}/crear-prospecto`, config);
      let json = await res.json();
      console.log(json);
      //   this.props.history.push("/");
    } catch (error) {
      this.setState({
        loading: false,
        error,
      });
    }
  };

  render() {
    return (
      <Fragment>
        <Encabezado titulo="Nuevos Prospectos" />
        <div className="row">
          <div className="card card-primary card-outline">
            <div className="card-header"></div>
            <div className="card-body">
              <ProspectoForm
                onChange={this.handleChange}
                form={this.state.form}
                onSubmit={this.handleSubmit}
              />
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default ProspectoNew;
