import React from "react";

const Filas = ({ indice, prospecto }) => {
  const mostrarObservaciones = (estado) => {
    if (estado !== "Rechazado") {
      return;
    }
    return prospecto.Prospect_Observ;
  };

  return (
    <tr>
      <td>{indice}</td>
      <td>{prospecto.Prospect_Name}</td>
      <td>{prospecto.Prospect_FName}</td>
      <td>{prospecto.Prospect_LName}</td>
      <td>{prospecto.Prospect_Status}</td>

      <td>{mostrarObservaciones(prospecto.Prospect_Status)}</td>
      <td>
        <div className="btn-group">
          <button
            className="btn btn-primary rounded-circle mr-2 "
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            <i class="fas fa-user-tie"></i>
          </button>

          <button className="btn btn-warning rounded-circle mr-2">
            <i className="nav-icon fas fa-pencil-alt"></i>
          </button>

          <button className="btn btn-danger rounded-circle">
            <i className="nav-icon fas fa-trash"></i>
          </button>
        </div>
      </td>
    </tr>
  );
};

export default Filas;
