  //     } catch (error) {
  //       setError(error);
  //     }
  //   };
  //   fetchResource();
  // }, [prospecto]);

  async componentDidMount() {
    await this.fetchProspecto(this.props.prospecto);
  }

  fetchProspecto = async (prospecto) => {
    let res = await fetch(`${rutaAPI}/mostrar-prospecto/${prospecto}`);
    let data = await res.json();

    if (data.status == 200) {
      let data = data.data;
      this.setState({ data });
    }
  };

  render() {
    return <ProspectoModal {...data} />;
  }
}

export default Modalcontainer;
