import React from "react";
import { useState, useEffect } from "react";
import { rutaAPI } from "../../config/Config";

import ProspectoModal from "./presentation/ProspectoModal";
import $ from "jquery";

export default function Modalcontainer({ prospecto, history }) {
  const [data, setData] = useState({});
  const [error, setError] = useState(null);
  const [observaCount, setObservaCount] = useState(0);

  useEffect(() => {
    const fetchResource = async () => {
      try {
        let res = await fetch(`${rutaAPI}/mostrar-prospecto/${prospecto}`);
        let data = await res.json();

        setData(data.data[0]);
      } catch (error) {
        setError(error);
      }
    };
    fetchResource();
  }, [prospecto]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    let config = {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    };
    let res = await fetch(`${rutaAPI}/edit-prospecto/${data._id}`, config);
    let json = await res.json();
    console.log(json);

    // if (json.status == 200) {
    //   history.push("/");
    // }
  };
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });

    let estado = e.target.value;

    console.log("estado", estado);
    if (
      e.target.name == "Prospect_Status" &&
      estado == "Rechazado" &&
      observaCount < 1
    ) {
      setObservaCount(observaCount + 1);

      $("#observaciones").fadeIn();
    } else {
      if (e.target.name == "Prospect_Status" && estado == "Autorizado") {
        $(".observaciones").fadeOut();
        setObservaCount(0);
      }
    }
  };

  const handleOnClick = () => {
    $(".observaciones").remove();
    setObservaCount(0);
  };

  return (
    <ProspectoModal
      onChange={handleChange}
      onSubmit={handleSubmit}
      onClick={handleOnClick}
      {...data}
    />
  );
}
